const Example = () => {

  const [ state, setState ] = useState(0)
  const [ count, dispatch ] = useReducer()

  const countUp = () => {
    setState(prev => ++prev)
  }

  return (
    <>
      <h3>{state}</h3>
      <button onClick={countUp}>+</button>
    </>
  );
};

export default Example;
